import "./index.css";
const App = () => {



const [color, setcolor] =useState(newstyle);
const [mode, setmode] =useState(mystyle);
const mystyle = {
  color: "white",
  margin:0,
    padding:0,
  backgroundColor: "black",
   width:"100%",
   height:"100vh",
   textAlign:"center",
   display: 'flex',
   justifyContent:' center',
   alignItems: 'center',
};
const newstyle = {
  display: 'flex',
  justifyContent:' center',
  alignItems: 'center',
  backgroundColor: "white",
   width:"100%",
   height:"100vh",
   textAlign:'center'

};

const change = () =>{
  
    setcolor(mystyle);


};
const changemode = () =>{
  
  setmode(newstyle);


};
  return (
  <>
<div style={color,mode}>
  <div>
  <h1>hello every one</h1>
<button onClick={change}> switch to dark mode </button>
<button onClick={changemode}> switch to light mode </button>
  </div>
  
  


</div>
  </>
  );
};

export default App;
