import React from 'react'
import { AiFillDelete } from 'react-icons/ai'
import './note.css'
const Note = ({data,setData}) => {

  useEffect(()=>{
    const fetch_memo = JSON.parse(localStorage.getItem('memo_app'))
    setData(fetch_memo)
  },[])


  const Delete = (id) => {
    let fetch_memo =  JSON.parse(localStorage.getItem('memo_app'))
    fetch_memo.filter((item)=> item.id !== id)
    localStorage.setItem('memo_app',JSON.stringify(fetch_memo))
  setData(fetch_memo)
  }


  return (
    <div className='note_css'>
      <h3 className='title'> {data.title} </h3>
      <p className='memo'>
        {data.memo}
      </p>
      <div className='note_footer'>
        <p>{data.date}</p>
        <button 
        className='delete_btn'
      onChange={()=>{Delete(data.id)}}
        >
          <AiFillDelete className='del_icon' />
          </button>
      </div>
    </div>
  )
}

export default Note
