import React, { useState } from 'react';

const Student = () => {
    const [status,setstatus]=useState(true);

    const show=()=>{
        setstatus(false);
    }
    const hide=()=>{
        setstatus(true);
    }
    const toggle=()=>{
        setstatus(!status);
    }
  return(
  <>
    {status ? <h1>hide this line</h1>:null}
<button onClick={show}>hide</button>
<button onClick={hide}>show</button>
<button onClick={toggle}>toggle</button>
  </>
  );
};

export default Student;
