import React, {useEffect} from 'react'
import './list.css'

import Note from './Note'
const List = ({array_data,setData}) => {

   // fetching memo from localStorage

   useEffect(()=>{
    const fetch_memo = JSON.parse(localStorage.getItem('memo_app'))
    setData(fetch_memo)
  },[])

//  delete data from localStorage

const Delete = (id) => {
  let fetch_memo =  JSON.parse(localStorage.getItem('memo_app'))
  fetch_memo.filter((item)=> item.id !== id)
  localStorage.setItem('memo_app',JSON.stringify(fetch_memo))
setData(fetch_memo)
}

  return (

    <div className='list_css'>
     {
      array_data.map((element)=> <Note
      key={element.id}
      data={element}
      Delete={Delete}
      />)
     }




    </div>
  )
}

export default List
