import React, { useState } from 'react';

const Form = () => {
    const [name,setname]=useState({
        sname:'',
        fname:''
    });
  
    const get=(e)=>{
        setname(e.target.value);
        const name=e.target.name;
        const val=e.target.value;
    }
   
    const submit=(e)=>{
       e.preventDefault();
       
          alert('form submitted')
    }
  return(
   <>
   <form action="" onSubmit={submit}>
   <input type="text" value={name.sname} onChange={get} name='sname'  placeholder="enter your name"/>
   <br />
   <input type="text"  value={name.fname} onChange={get} name='fname' placeholder='enter your father name'/>
   <br />
   <button type='submit'>send</button>
   <br />
   <h1> {name.sname} s/o {name.fname}</h1>
   </form>
   </>
  );
};

export default Form;
