const App = () => {




const [color, setcolor] =useState();
const newstyle = {
  color: "red",
  backgroundColor: "white",
   width:"100%",
   height:"100vh"
};
const change = () =>{
setcolor(newstyle);
};
  return (
  <>
<div style={color}>

<h1>hello every one
</h1>
<button onClick={change}> click</button>
</div>
  </>
  );
};

export default App;
