import React, { useState } from 'react';
import './index.css';
const Form = () => {
    const [name,setname]=useState('');
    const [fullname,setfullname]=useState('');
    const [email,setemail]=useState('');
    const [fullemail,setfullemail]=useState('');
    const [pwd,setpwd]=useState('');
    const [fullpwd,setfullpwd]=useState('');
    

    const input1=(e)=>{
        setname(e.target.value)
    }
    const input2=(e)=>{
        setemail(e.target.value)
    }
    const input3=(e)=>{
        setpwd(e.target.value)
    }
    const submit=(e)=>{
     e.preventDefault();
     setfullname(name);
     setfullemail(email);
     setfullpwd(pwd);
  
    }

  return(
   <>
   <div className='form_div'>
  <div className='form_sec_div'>
  <form action="" onSubmit={submit}>
   
      <input type="text" onChange={input1} value={name} placeholder='enter your name '/> <br />
      <input type="email" onChange={input2} value={email} placeholder='enter your email' /><br />
      <input type="password" onChange={input3} value={pwd}  placeholder='enter your password'/><br />
      <input type="submit" /><br />
      <h3>{fullname} {fullemail} {fullpwd} </h3>

      </form>
  </div>
      </div>
  </>
  );
};

export default Form;
