import React, { useState } from 'react';

const Mode = () => {

    const myStyle = {
        color: "white",
        backgroundColor: "DodgerBlue",
        padding: "10px",
        fontFamily: "Sans-Serif",
        width:'500px',
        height:'500px',
        margin:'100px'
      };
      
     const [dark,setdark]=useState(myStyle);
       const change=()=>{
           setdark(!dark);
       }
  return(
  <>
<div style={dark}>
<h3>
    hello dark mode
    <button onClick={change}>mode</button>
</h3>
</div>
  </>
  );
};

export default Mode;
