import React, { useEffect, useState } from 'react'
import List from './List'
import './main.css'
import Data from '../Data'
import NewNote from './NewNote'
import { nanoid } from "nanoid"


const Main = () => {

  const [data, setData] = useState(Data)

  // add new memo to form 

  const addnewdata = (val) => {
    const date = new Date()
    const obj =
     {
      id: nanoid(),
      title: val.title,
      memo: val.memo,
      date: date.toLocaleDateString('en-us', { weekday: "long", year: "numeric", month: "short", day: "numeric" })
    }
    const newval = [...data, obj]
    localStorage.setItem('memo_app', JSON.stringify(newval))
    setData(newval)
  }

  // fetching memo from localStorage
  useEffect(()=>{
    const fetch_memo = JSON.parse(localStorage.getItem('memo_app'))
    setData(fetch_memo)
  },[])

  return (
    <div className='main_css'>
      <NewNote addnewdata={addnewdata} />
      <List array_data={data} />
    </div>
  )
}

export default Main
