import React, { useState } from 'react';
import "./index.css";
const App = () => {

  const newstyle = {
    display: 'flex',
    justifyContent:' center',
    alignItems: 'center',
    backgroundColor: "white",
     width:"100%",
     height:"100vh",
     textAlign:'center'

  };

const [color, setcolor] =useState(newstyle);

const mystyle = {
  color: "white",
  margin:0,
    padding:0,
  backgroundColor: "black",
   width:"100%",
   height:"100vh",
   textAlign:"center",
   display: 'flex',
   justifyContent:' center',
   alignItems: 'center',
};

const change = () =>{
setcolor(mystyle);
};
  return (
  <>
<div style={color}>
  <div>
  <h1>hello every one</h1>
<button onClick={change}> dark mode </button>
  </div>



</div>
  </>
  );
};

export default App;
