import React, { useState } from 'react';
import './index.css';
const Form = () => {
    const [name,setname]=useState('');
    const [fullname,setfullname]=useState('');
    const [email,setemail]=useState('');
    const [fullemail,setfullemail]=useState('');
    const [pwd,setpwd]=useState('');
    const [fullpwd,setfullpwd]=useState('');
    const [select,setselect]=useState('');
    const [fullselect,setfullselect]=useState('');

    const input1=(e)=>{
        setname(e.target.value)
    }
    const input2=(e)=>{
        setemail(e.target.value)
    }
    const input3=(e)=>{
        setpwd(e.target.value)
    }
    const input4=(e)=>{
        setselect(e.target.value)
    }
    const submit=(e)=>{
     e.preventDefault();
     setfullname(name);
     setfullemail(email);
     setfullpwd(pwd);
     setfullselect(select);
     setemail('');
     setname('');
     setpwd('');
     setselect('');
    }

  return(
   <>
   <div className='form_div'>
  <div className='form_sec_div'>
  <form action="" onSubmit={submit}>
   <h2>Simple Form</h2>
      <input  className='input' type="text" onChange={input1} value={name} placeholder='enter your name '/> <br />
      <input  className='input' type="email" onChange={input2} value={email} placeholder='enter your email' /><br />
      <input  className='input' type="password" onChange={input3} value={pwd}  placeholder='enter your password'/><br />
      <select name="" id=""  onChange={input4} value={select} >
      <option value="">select</option>
      <option value="adil">adil</option>
      <option value="dawood">dawood</option>
      <option value="inam">inam</option>
      <option value="qasim">qasim</option>
      </select> <br />
      <input className='check' type="checkbox" /> 
      <span>i accept the terms and agreement</span> <br />
      <input  className='input' type="submit" /><br />
      <h3> Name:{fullname} </h3>
      <h3> Email:{fullemail} </h3> 
      <h3>Password: {fullpwd}</h3>
      <h3>select option: {fullselect}</h3>
      </form>
  </div>
      </div>
  </>
  );
};

export default Form;
