     width:"100%",
     height:"100vh"
  };

const [color, setcolor] =useState(newstyle);

const mystyle = {
  color: "white",
  margin:0,
    padding:0,
  backgroundColor: "black",
   width:"100%",
   
};

const change = () =>{
setcolor(mystyle);
};
